import numpy as np
import pickle
import streamlit as st

# loading the saved model
loaded_model = pickle.load(open('D:\placement/MINOR/trained_model 1.sav' , 'rb'))

# creating a function for Prediction
def placement_prediction(Branch, Gender, tenth_percentage, twelfth_percentage, CGPA_Till_sixth, sixth_Sem_SGPA, Internship, Skills):
    # convert inputs to appropriate data types
    tenth_percentage = float(tenth_percentage)
    twelfth_percentage = float(twelfth_percentage)
    CGPA_Till_sixth = float(CGPA_Till_sixth)
    sixth_Sem_SGPA = float(sixth_Sem_SGPA)

    # convert categorical inputs to one-hot encoding
    branches = ['Bio-Medical Engineering', 'Civil Engineering', 'Computer Science & Engineering','Electrical Engineering', 'Electronics & Communication Engineering','Electronics & Instrumentation Engineering', 'Mechanical Engineering', 'Petro Chemical']
    genders = ['Male', 'Female']
    internships = ['Yes', 'No']
    skills = ['Machine Learning/AI', 'Python', 'Data Science', 'java', 'C++/C', 'DSA', 'SQL', 'MS-Office/MS-Word','UI/UX', 'IOT', 'Computer Vision', 'Cloud Computing', 'Graphic Designing', 'Web Development ']

    branch_idx = branches.index(Branch)
    gender_idx = genders.index(Gender)
    internship_idx = internships.index(Internship)
    
    skills_one_hot = np.zeros(len(skills))

    for skill in Skills:
        try:
            skills_one_hot[skills.index(skill)] = 1
        except ValueError:
            pass

    # create input array
    input_data = np.array([branch_idx, gender_idx, tenth_percentage, twelfth_percentage,
                           CGPA_Till_sixth, sixth_Sem_SGPA, internship_idx, *skills_one_hot])
    input_data = input_data.reshape(1, -1)
    # make prediction and return result
    prediction = loaded_model.predict(input_data)
    print(prediction)

    if (prediction[0] == 0):
      return 'The person is not placed'
    else:
      return 'The person is placed'
   



def main():
    
    # giving a title
    st.title('PLACEMENT PREDICTION WEBAPP')
    
    st.markdown(
    """
    <style>
   
    .main{
        background: url('data:image/jpeg;base64,');
        background-repeat: no-repeat;
        background-size: cover;
        width:100vw;
        height:100vh;
        }
    </style>
    """,
    unsafe_allow_html=True
)
   
    
    
    # getting the input data from the user
    Branch=st.selectbox('Provide your branch',['Bio-Medical Engineering', 'Civil Engineering', 'Computer Science & Engineering', 'Electrical Engineering', 'Electronics & Communication Engineering', 'Electronics & Instrumentation Engineering', 'Mechanical Engineering', 'Petro Chemical'])
    Gender = st.selectbox('What is your gender?', ['Male', 'Female'])
    tenth_percentage = st.number_input('What was your percentage in 10th class?',format='%f',min_value=0.0, max_value=100.0,step=0.1)
    twelfth_percentage = st.number_input('What was your percentage in 12th class?',format='%f',min_value=0.0,max_value=100.0,step=0.1)
    CGPA_Till_sixth = st.number_input('What is your CGPA?',min_value=0.0, max_value=10.0)
    sixth_Sem_SGPA = st.number_input('What was your SGPA in the 6th semester?',min_value=0.0 , max_value=10.0)
    Internship = st.radio('Have you done any internship?', ['Yes', 'No'])
    Skills = st.multiselect('Provide your skills',['Machine Learning/AI','Python','Data Science','java','C++/C','DSA','SQL','MS-Office/MS-Word','UI/UX','IOT','Computer Vision','Cloud Computing','Graphic Designing','Web Development '])

  
    
    
    # code for Prediction
    placement = ''
    
    # creating a button for Prediction
    
    if st.button('placement Test Result'):
        placement = placement_prediction(Branch,Gender,tenth_percentage,twelfth_percentage,CGPA_Till_sixth,sixth_Sem_SGPA,Internship,Skills)
        
        
    st.success(placement)
    
        
if __name__ == '__main__':
    main()
    